import MaterialTable from 'material-table'
import React from 'react'
import { BASICDATA } from '../util/dataFromFile'
import { CSVReader } from 'react-papaparse'

export default class BasicSelection extends React.Component {
  constructor (props) {
    super(props)
    this.state = { data: '' }
  }

  componentDidMount () {
    this.setState({
      data: BASICDATA
    })
  }

  handleOnFileLoad() {
    this.setState(data)
  }

  render () {
    return (
      <>
        <CSVReader
        ref={buttonRef}
        onFileLoad={this.handleOnFileLoad}
        onError={this.handleOnError}
        noClick
        noDrag
        onRemoveFile={this.handleOnRemoveFile}
      >
        {({ file }) => (
          <aside
            style={{
              display: 'flex',
              flexDirection: 'row',
              marginBottom: 10
            }}
          >
            <button type='button' onClick={this.handleOpenDialog}>
              Browe file
            </button>
            <div>{file && file.name}</div>
            <button onClick={this.handleRemoveFile}>Remove</button>
          </aside>
        )}
      </CSVReader>
        <MaterialTable
          title='Select column'
          columns={[
            { title: 'Column Name', field: 'columnname' },
            { title: 'Variable type', field: 'variabletype' }
          ]}
          data={this.state.data}
          options={{
            selection: true
          }}
        />{' '}
      </>
    )
  }
}
